mapper.delete:
delete from t_video_collection
where video_id = #{videoId}
  and user_id = #{userId}
  and group_id = #{groupId}

mapper.select:
select *
from t_video_like
where video_id = #{videoId}
and user_id = #{userId}

select count(*) as count
from t_video_like
where video_id = #{videoId}

select *
from t_video_collection
where video_id = #{videoId}
and user_id = #{userId}
and group_id = #{groupId}

select count(*) as count
from t_video_collection
where video_id = #{videoId}

select *
from t_video_coin
where video_id = #{videoId}
and user_id = #{userId}

select distinct user_id
from t_video_like
where video_id = #{videoId}

select *
from t_video_collection
where video_id = #{videoId}
  and user_id = #{userId}

select distinct user_id
from t_video_collection
where video_id = #{videoId}
